import React, { useState } from 'react'
import Header from "../Common/header";
import Footer from "../Common/footer";
import { Button, Card, Input, Modal, Select, Switch, Tooltip } from 'antd';
import { compact } from 'lodash';
import { TABLE_LIMIT } from '../../Common/constant';
import TableComponent from "../Common/TableComponent";
const Client = (props) => {
    const [showModal, setShowModal] = useState(false);
    const [data, setData] = useState([]);
    const [count, setCount] = useState([]);
    const [searchValue, setSearchValue] = useState(); // search
    const [skip, setSkip] = useState(0); // table skip
    const [page, setPage] = useState(1); // table current page
    const [loading, setLoading] = useState(false);
    const { history } = props;

    const onEditHandle = (data) => {
        const newData = { ...data };
        if (data?.platform_configs) {
          const provider = last(Object.keys(data?.platform_configs));
          newData.provider = provider;
          let services;
          let formField;
          Object.keys(data?.platform_configs).map((item) => {
            services = last(Object.keys(data?.platform_configs[item]));
            newData.services = services;
            return true;
          });
        }

        setShowModal(true);
      };

      const handleAdd = () => {
        setShowModal(true);
      };

      // for delete platform
      const onDeleteHandle = async (values) => {
        confirm({
          title: 'Are you sure you want to delete this sender?',
          okText: 'Yes',
          cancelText: 'No',
          centered: true,
          onOk: () => {
            const variables = { id: values?.id, isDisabled: true };
            try {
              client.mutate({
                mutation: UPDATE_SENDER,
                variables: variables
              });
              reloadData();
            } catch (error) {
              return error;
            }
          }
        });
      };

    // set column for table
    const columns = [
        {
            title: 'Name',
            dataIndex: 'name',
            width: '10%'
        },
        {
            title: 'Email',
            dataIndex: 'email',
            width: '10%'
        },
        {
            title: 'Phone No',
            dataIndex: 'phoneNo',
            width: '10%'
        },
        {
            title: 'Action',
            dataIndex: 'id',
            width: '10%',
            render: (value, record) => {
                return (
                    <div className="action-icons">
                        <Tooltip title="Edit">
                            <Button type="link" onClick={() => onEditHandle(record)}>
                                <EditOutlined />
                            </Button>
                        </Tooltip>
                        <Tooltip title="Delete">
                            <Button type="link" danger onClick={() => onDeleteHandle(record)}>
                                <DeleteOutlined />
                            </Button>
                        </Tooltip>
                    </div>
                );
            }
        }
    ];


    return (<React.Fragment>
        <Header />
        <div className="content">
            <div className="d-flex">
                <h1>Client</h1>
                <Button>Add</Button>
            </div>

            <div>
                <TableComponent
                    loading={loading}
                    list={data}
                    columns={columns}
                    current={page}
                    pageSize={TABLE_LIMIT}
                    total={count}
                    handlePageChange={handlePageChange}
                />
            </div>

        </div>
        <Footer />
    </React.Fragment>
    )
}

export default Client
